import { useState } from "react";

const safetyProducts = [
  { id: 1, name: "Pepper Spray", image: "data:image/jpeg;base64,", 
  price: 500, quantity: 0 },
  { id: 2, name: "Personal Alarm", image: "data:image/jpeg;base64,",
    price: 100, quantity: 0 },
  { id: 3, name: "Self-Defense Keychain", image: "data:image/jpeg;base64,", 
    price: 600, quantity: 0 },
  { id: 4, name: "Stun Gun", image: "data:image/jpeg;base64,",
     price: 1000, quantity: 0 },
  { id: 5, name: "Tactical Pen", image: "https://m.media-amazon.com/images/I/61DxwyklbuL._AC_UF894,1000_QL80_.jpg",
     price: 900, quantity: 0 },
  { id: 6, name: "Door Stop Alarm", image: "data:image/jpeg;base64,", 
    price: 1500, quantity: 0 }
];

export default function SafetyProducts() {
  const [products, setProducts] = useState(safetyProducts);

  const updateQuantity = (id, delta) => {
    setProducts(products.map(product => 
      product.id === id ? { ...product, quantity: Math.max(1, product.quantity + delta) } : product
    ));
  };

  return (
    <div className="p-6  min-h-screen">
      <h2 className="text-3xl font-bold text-center mb-6 text-gray-800">Safety Products </h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 max-w-6xl mx-auto">
        {products.map(({ id, name, image, price, quantity }) => (
          <div key={id} className="bg-white border p-4 rounded-lg shadow-lg">
            <img src={image} alt={name} className="w-full h-40 object-cover mb-4 rounded-md" />
            <h3 className="text-xl font-semibold text-gray-900">{name}</h3>
            <p className="text-lg text-gray-700 mb-2">${price.toFixed(2)}</p>
            <div className="flex items-center my-2 justify-center">
              <button 
                onClick={() => updateQuantity(id, -1)} 
                className="px-3 py-1 bg-black-300 text-white rounded-l hover:bg-gray-400 "
              >-</button>
              <span className="px-4">{quantity}</span>
              <button 
                onClick={() => updateQuantity(id, 1)} 
                className="px-3 py-1 bg-black-300 text-white rounded-r hover:bg-gray-400"
              >+</button>
            </div>
            <button className="mt-4 w-full bg-black text-white py-2 rounded-lg font-semibold hover:bg-gray-900 transition">
  Buy Now
</button>
</div>
        ))}
      </div>
    </div>
  );
}
